import React, { Component } from 'react';

class Paginator extends Component {
     constructor(props) {
        super(props);
        this.state = { 
            pageArr: new Array(this.props.totalPage).fill({}),
            activePage: 0
         }
        }

    // onPressIndex = (pageNumber) => {
    //     const {onChange} = this.props;
    //     // console.log('11111', pageNumber);
    //     onChange();
    // }

    componentDidMount() {
        this.setState({
            activePage: this.props.activePage
        })
        let pageArr = []
        for (let i = 0; i < this.props.totalPage; i++) {
            pageArr.push({})
        }
        console.log('33333 -> paginater', pageArr, this.props.totalPage)
        this.setState(pageArr)
    }

    componentDidUpdate(prevState) {
        console.log('222222 -> paginater', this.props.totalPage, this.state.pageArr)

        // if (!this.state.pageArr) {
        //     // this.setState({
        //     //     pageNumber: new Array(this.props.totalPage).fill({})
        //     // })
        //     console.log('11111 -> paginater', this.props.totalPage)

        // }
    }

    render() { 
        const {totalPage, activePage} = this.props;

        return ( 
            <div style={{
            display: 'flex', 
            marginTop: '5%',
            flexDirection: 'row', 
            justifyContent: 'center'}}>
               {this.state.pageArr && this.state.pageArr.map((page, i) => (
                   <div 
                   //onClick={() => this.props.onChange(i + 1)} 
                   key={i+1} style={{
                 border: '1px solid #cdcdcd',
                   margin: '1%',
                   borderRadius: 4,
                   width: '45px',
                   cursor: 'pointer', 
                   backgroundColor: i + 1 == activePage ? '#cdcdcd' : 'white'
                   }}>
                   <p style={{ fontSize: 20, 

                    paddingTop: '14px', 
                    paddingLeft: '14px'
                }}>{i + 1}</p>
                   </div>
               ))}
            </div>
         );
    }
}

export default Paginator;
