import React, { Component } from 'react';
import {months} from '../constants';
import axios from "axios";

class PieChart extends Component {
    constructor(props) {
        super(props);
        this.state = { 
            monthlyData: []
         }
        }

        componentDidMount = async () => {
            const {data, userName} = this.props; 
            let url = `https://api.github.com/repos/${userName}/${data.name}/stats/commit_activity`;
            let respJson = await axios.get(url);
            let commitActivity = respJson.data;
            console.log(commitActivity);
            let monthly_data = [];
            if(commitActivity.length > 0) {
              for (let i = 0; i < 12; i++) {
                let commit_sum = 0;
                for (let j = 0; j < 4; j++) {
                  commit_sum = commit_sum + commitActivity[4 * i + j].total;
                }

                let temp = {
                  month: months[i],
                  commit: commit_sum,
                };
                monthly_data.push(temp);
              }
            }  
            this.setState({
                monthlyData: monthly_data,
            });

          };

    render() { 
        return ( 
           <div>
               <p>Hello World</p>
           </div> 
         );
    }
}

export default PieChart;
