import React, { Component } from 'react';
import {
    Chart,
    BarSeries,
    Title,
    ArgumentAxis,
    ValueAxis,
  } from "@devexpress/dx-react-chart-material-ui";
  import axios from "axios";

  import { Animation } from "@devexpress/dx-react-chart";

class CommitDetails extends Component {
    state = { 
        commitsData: null,
        data: []
     }

    async componentDidMount() {
        const {data, userName} = this.props;
        let response = await axios.get(`https://api.github.com/repos/${userName}/${data.name}/stats/participation`);
        let commitsData = response.data.all;
        // fetch(
        //     `https://api.github.com/repos/${userName}/${data.name}/stats/participation`
        //   )
        //     .then(response => response.json())
        //     .then(responseData => {
        //         commitsData = responseData.all
        //         console.log('111111 json', commitsData)
        //       this.setState({
        //         commitsData: responseData.all
        //       });
        //     });

            for (let i = 0; i < commitsData.length; i++) {
                console.log('111111 json', this.state.data)
                this.state.data.push({
                  week: "week-" + (i + 1),
                  commit: commitsData[i],
                });
              }
    }

    render() { 
        const {data} = this.props;
        //console.log('1111 commit details', this.state.data)
        if (data && this.state.data.length) {
        return ( <div>
           <Chart
          data={this.state.commitsData.slice(
            0,
            9 + 1
          )}
        >
          <ArgumentAxis />
          <ValueAxis
            max={
              10
            }
          />

          <BarSeries valueField="commit" argumentField="week" barWidth={1} />
          <Title text="Participation Stats" />
          <Animation />
        </Chart>
        </div> );
        } 
        return null;
    }
}

export default CommitDetails;
