import React, { Component } from 'react';
import {
    Chart,
    BarSeries,
    Title,
    ArgumentAxis,
    ValueAxis,
  } from "@devexpress/dx-react-chart-material-ui";
  import Paper from "@material-ui/core/Paper";
  import axios from "axios";
  import Pagination from "react-js-pagination";
  import Paginator from './Paginator';
  import { Animation } from "@devexpress/dx-react-chart";

class CommitDetails extends Component {

     constructor(props) {
        super(props);
        this.state = { 
            commitsData: null,
            dataList: [],
            activePage: 0,
            total_pages: 0,
            shouldRender: false
         }
         this.handlePageChange = this.handlePageChange.bind(this);
        }

     getCurrentPageLastIndex() {
        const last_index = this.state.dataList.length - 1;
        let page_last_index =
          last_index - 10 * (this.state.total_pages - this.state.activePage);
        return page_last_index;
      }

      getCurrentPageFirstIndex() {
        const page_last_index = this.getCurrentPageLastIndex();
        if (page_last_index - 9 < 0) {
          return 0;
        }
        return page_last_index - 9;
      }


    async componentDidMount() {
        const {data, userName} = this.props;
        let dataList = [];
        let response = await axios.get(`https://api.github.com/repos/${userName}/${data.name}/stats/participation`);
        let commitsData = response.data.all;


            for (let i = 0; i < commitsData.length; i++) {
                //console.log('111111 json', this.state.data)
                dataList.push({
                  week: "WEEK " + (i + 1),
                  commit: commitsData[i],
                });
              }
              this.setState({
                  dataList: dataList,
                  activePage: Math.ceil(dataList.length / 10),
                  total_pages: Math.ceil(dataList.length / 10),
                  shouldRender: true
              })

    }

    handlePageChange(pageNumber) {
        console.log(`active page is ${pageNumber}`);
        this.setState({activePage: pageNumber});
      }

    render() { 
        const {data} = this.props;
        //console.log('1111 commit details', this.state.dataList)

        return ( 
        <div key={this.state.activePage} style={{ width: '100%', padding: '5%'}}>
           <Chart
          data={this.state.dataList.slice(
            this.getCurrentPageFirstIndex(),
            this.getCurrentPageLastIndex() + 1
          )}
        >
          <ArgumentAxis />
          <ValueAxis
            max={
                this.getCurrentPageLastIndex() -
                this.getCurrentPageFirstIndex() +
                1
            }
          />

          <BarSeries valueField="commit" argumentField="week" barWidth={1} color={'green'}/>
          <Title text="Yearly Activity"/>
          <Animation />
        </Chart>
        {this.state.activePage ? (
        <Paginator 
            totalPage={this.state.total_pages}
            activePage={this.state.activePage}
            onChange={this.handlePageChange}
        />) : null}
        </div>);
    }
}

export default CommitDetails;
