import React, { Component } from 'react';
import {
    Chart,
    BarSeries,
    Title,
    ArgumentAxis,
    ValueAxis,
  } from "@devexpress/dx-react-chart-material-ui";
  import Paper from "@material-ui/core/Paper";
  import axios from "axios";
  import Pagination from "react-js-pagination";
//require("bootstrap/less/bootstrap.less");

  import { Animation } from "@devexpress/dx-react-chart";

class CommitDetails extends Component {
    state = { 
        commitsData: null,
        dataList: [
            // {"week": 0, "commit": 22},
            // {"week": 1, "commit": 12},
            // {"week": 2, "commit": 52},
            // {"week": 3, "commit": 10},
            // {"week": 4, "commit": 3},
            // {"week": 5, "commit": 22},
            // {"week": 6, "commit": 12},
            // {"week": 7, "commit": 52},
            // {"week": 8, "commit": 10},
            // {"week": 9, "commit": 3},
        ],
        activePage: 0,
        total_pages: 0,
     }

     getCurrentPageLastIndex() {

        const last_index = this.state.dataList.length - 1;
        console.log('22222', last_index)
        let page_last_index =
          last_index - 10 * (this.state.total_pages - this.state.activePage);
          console.log('22222', page_last_index)
        return page_last_index;
      }

      getCurrentPageFirstIndex() {
        const page_last_index = this.getCurrentPageLastIndex();
        if (page_last_index - 9 < 0) {
          return 0;
        }
        return page_last_index - 9;
      }


    async componentDidMount() {
        const {data, userName} = this.props;
        let dataList = [];
        let response = await axios.get(`https://api.github.com/repos/${userName}/${data.name}/stats/participation`);
        let commitsData = response.data.all;

        //console.log('1111 commit details', this.state.dataList, commitsData)
        // fetch(
        //     `https://api.github.com/repos/${userName}/${data.name}/stats/participation`
        //   )
        //     .then(response => response.json())
        //     .then(responseData => {
        //         commitsData = responseData.all
        //         console.log('111111 json', commitsData)
        //       this.setState({
        //         commitsData: responseData.all
        //       });
        //     });

            for (let i = 0; i < commitsData.length; i++) {
                //console.log('111111 json', this.state.data)
                dataList.push({
                  week: "week-" + (i + 1),
                  commit: commitsData[i],
                });
              }
              this.setState({
                  dataList: dataList,
                  total_pages: Math.ceil(dataList.length / 10),
              })

    }

    handlePageChange(pageNumber) {
        console.log(`active page is ${pageNumber}`);
        this.setState({activePage: pageNumber});
      }

    render() { 
        const {data} = this.props;
        console.log('1111 commit details', this.state.dataList)

        return ( 
        <div style={{ width: '100%', border: '1px solid black' }}>
           <Chart
          data={this.state.dataList.slice(
            this.getCurrentPageFirstIndex(),
            this.getCurrentPageLastIndex() + 1
          )}
        >
          <ArgumentAxis />
          <ValueAxis
            max={
                this.getCurrentPageLastIndex() -
                this.getCurrentPageFirstIndex() +
                1
            }
          />

          <BarSeries valueField="commit" argumentField="week" barWidth={1} />
          <Title text="Participation Stats" />
          <Animation />
        </Chart>
        <Pagination
          activePage={this.state.activePage}
          itemsCountPerPage={10}
          totalItemsCount={this.state.dataList.length}
          onChange={this.handlePageChange.bind(this)}
        />
        </div>);
    }
}

export default CommitDetails;
