import React, { Component } from 'react';

class Paginator extends Component {
     constructor(props) {
        super(props);
        this.state = { 
            pageArr: new Array(props.totalPage).fill({}),
         }
        }

    onPressIndex = (page) => {
        const {onChange} = this.props;
        console.log('11111', page);
        onChange(page);
    }

    render() { 
        const {totalPage, activePage} = this.props;

        return ( 
            <div style={{
            display: 'flex', 
            marginTop: '5%',
            flexDirection: 'row', 
            justifyContent: 'center'}}>
               {this.state.pageArr.map((page, i) => (
                   <div 
                   onClick={() => this.onPressIndex(i + 1)} 
                   key={i} style={{
                 border: '1px solid #cdcdcd',
                   margin: '1%',
                   borderRadius: 4,
                   width: '45px',
                   cursor: 'pointer', 
                   backgroundColor: i + 1 == activePage ? '#cdcdcd' : 'white'
                   }}>
                   <p style={{ fontSize: 20, 

                    paddingTop: '14px', 
                    paddingLeft: '14px'
                }}>{i + 1}</p>
                   </div>
               ))}
            </div>
         );
    }
}

export default Paginator;
