import React, { Component } from 'react';

class Paginator extends Component {
     constructor(props) {
        super(props);
        this.state = { 
            pageArr: new Array(props.totalPage).fill({}),
         }
        }

    render() { 
        const {totalPage, activePage} = this.props;
        console.log('11111', totalPage, activePage, this.state.pageArr);
        return ( 
            <div style={{
            display: 'flex', 
            marginTop: '5%',
            flexDirection: 'row', 
            justifyContent: 'center'}}>
               {this.state.pageArr.map((page, i) => (
                   <div  
                   key={i} style={{border: '1px solid black', 
                   margin: '1%',
                   alignItems: 'center', 
                   cursor: 'pointer', }}>
                   <p style={{paddingHorizontal: '6px', fontSize: 20}}>{i + 1}</p>
                   </div>
               ))}
            </div>
         );
    }
}

export default Paginator;
